<?$who=$_COOKIE['VisitorName'];if(isset($who)&$_POST['url']!==null){include('config.php');$url=$_POST['url'];$action=$_POST['action'];if ($action=='like'){$fer=mysql_query("SELECT * FROM slikers WHERE url='$url' and user='".$who."'");if(mysql_num_rows($fer)==0){mysql_query("INSERT INTO slikers(url,user) VALUES(\"$url\",'$who')");mysql_query("UPDATE slikes SET likes=likes+1 WHERE url='$url'");}else{echo "<script>alert('You already liked this');</script>";}}if ($action=='unlike'){mysql_query("DELETE FROM slikers WHERE url=\"$url\" and user='$who'");mysql_query("UPDATE slikes SET likes=likes-1 WHERE url=\"$url\"");}}else{echo"Error";}?>
